import React from 'react'
import Table from 'react-bootstrap/Table';
import { confirmAlert} from 'react-confirm-alert';
import 'react-confirm-alert/src/react-confirm-alert.css'
import Button from 'react-bootstrap/Button';


const UserTable = props => (
	<Table striped bordered hover variant="thead-dark" >
		<thead class="rado">
			<tr class="laza"><th scope="col">Id</th>
				<th scope="col"> Produit</th>
				<th scope="col">Prix</th>
				<th scope="col">Actions</th>
			</tr>
		</thead>
		<tbody>
			{props.users.length > 0 ? (
				props.users.map(user => (
					<tr class="j"key={user.id}>
						<td>{user.id}</td>
						<td>{user.name}</td>
						<td class="k">{user.username}</td>
						<td>
						<Button variant="danger"  onClick={()=> confirmAlert({

							title: ' Supression Produit',
							buttons: [
							{
								label: 'OUI',
								onClick: () => props.deleteUser(user.id)
							},
							{
								label: 'NON',

							}
							]
							})
							}
							>X</Button>

							<Button  variant="success" onClick={() => confirmAlert ({

								buttons:[
									{
									label:"OK",
									onClick: () =>props.editRow(user)
									},
									{
										label:"Annuler"
									}
								]})}                        >Edit</Button>

						</td>
					</tr>
				))
			) : (
				<tr>
					<td colSpan={3}></td>
				</tr>


			)}
		</tbody>
	</Table>

)
				(<button><Button class="l" as="input" type="submit" value="Total" /></button>)
export default UserTable
